* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

html, body {
	height: 100%;
}

body {
	font-family: ${1:'Segoe UI', Tahoma, Geneva, Verdana, sans-serif};
	line-height: 1.6;
	color: ${2:#333};
}

:root {
	/* 색상 */
	--primary-color: ${1:#3498db};
	--secondary-color: ${2:#2ecc71};
	--accent-color: ${3:#e74c3c};
	--text-color: ${4:#333333};
	--bg-color: ${5:#ffffff};

	/* 폰트 크기 */
	--font-small: ${6:0.875rem};
	--font-normal: ${7:1rem};
	--font-medium: ${8:1.25rem};
	--font-large: ${9:1.5rem};
	--font-xlarge: ${10:2rem};

	/* 간격 */
	--spacing-xs: ${11:0.25rem};
	--spacing-sm: ${12:0.5rem};
	--spacing-md: ${13:1rem};
	--spacing-lg: ${14:1.5rem};
	--spacing-xl: ${15:2rem};
}

.flex-container {
	display: flex;
	flex-direction: ${1:row};
	justify-content: ${2:center};
	align-items: ${3:center};
	flex-wrap: ${4:wrap};
	gap: ${5:10px};
}

.grid-container {
	display: grid;
	grid-template-columns: ${1:repeat(3, 1fr)};
	grid-template-rows: ${2:auto};
	grid-gap: ${3:20px};
	padding: ${4:10px};
}

@media (max-width: ${1:768px}) {
	${2}
}

/* 모바일 장치 */
@media (max-width: 576px) {
	${1}
}

/* 태블릿 */
@media (min-width: 577px) and (max-width: 768px) {
	${2}
}

/* 작은 데스크탑 */
@media (min-width: 769px) and (max-width: 992px) {
	${3}
}

/* 큰 데스크탑 */
@media (min-width: 993px) and (max-width: 1200px) {
	${4}
}

/* 초대형 화면 */
@media (min-width: 1201px) {
	${5}
}

@keyframes ${1:animationName} {
	0% {
		${2:transform: translateY(0);}
	}
	50% {
		${3:transform: translateY(-20px);}
	}
	100% {
		${4:transform: translateY(0);}
	}
}

.${5:animated-element} {
	animation: ${1:animationName} ${6:2s} ${7:ease-in-out} ${8:infinite};
}

.${1:element} {
	transition: ${2:all} ${3:0.3s} ${4:ease};
}

.${1:element}:hover {
	${5:transform: scale(1.1);}
}

.card {
	background-color: ${1:#fff};
	border-radius: ${2:8px};
	box-shadow: 0 ${3:2px} ${4:4px} rgba(0, 0, 0, 0.1);
	padding: ${5:20px};
	margin: ${6:10px};
	transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.card:hover {
	transform: translateY(-${7:5px});
	box-shadow: 0 ${8:10px} ${9:20px} rgba(0, 0, 0, 0.1);
}

.button {
	display: inline-block;
	background-color: ${1:#3498db};
	color: ${2:white};
	padding: ${3:10px} ${4:20px};
	border: none;
	border-radius: ${5:4px};
	cursor: pointer;
	transition: background-color 0.3s ease;
	text-decoration: none;
	font-weight: ${6:bold};
}

.button:hover {
	background-color: ${7:#2980b9};
}

.button:active {
	transform: translateY(${8:2px});
}

.navbar {
	display: flex;
	justify-content: ${1:space-between};
	align-items: center;
	background-color: ${2:#333};
	color: ${3:white};
	padding: ${4:10px} ${5:20px};
}

.navbar-menu {
	display: flex;
	gap: ${6:20px};
}

.navbar-link {
	color: ${7:white};
	text-decoration: none;
	font-weight: ${8:500};
	transition: color 0.3s ease;
}

.navbar-link:hover {
	color: ${9:#3498db};
}

.form-group {
	margin-bottom: ${1:15px};
}

.form-label {
	display: block;
	margin-bottom: ${2:5px};
	font-weight: ${3:500};
}

.form-input {
	width: 100%;
	padding: ${4:10px};
	border: ${5:1px} solid ${6:#ddd};
	border-radius: ${7:4px};
	transition: border-color 0.3s ease;
}

.form-input:focus {
	outline: none;
	border-color: ${8:#3498db};
	box-shadow: 0 0 0 ${9:2px} rgba(52, 152, 219, 0.2);
}

.table {
	width: 100%;
	border-collapse: collapse;
}

.table th,
.table td {
	padding: ${1:12px} ${2:15px};
	text-align: ${3:left};
	border-bottom: ${4:1px} solid ${5:#ddd};
}

.table th {
	background-color: ${6:#f2f2f2};
	font-weight: ${7:bold};
}

.table tr:hover {
	background-color: ${8:#f5f5f5};
}

.center-absolute {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
}

.center-flex {
	display: flex;
	justify-content: center;
	align-items: center;
}

.truncate {
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
	max-width: ${1:100%};
}

.multi-line-truncate {
	display: -webkit-box;
	-webkit-line-clamp: ${1:2};
	-webkit-box-orient: vertical;
	overflow: hidden;
	text-overflow: ellipsis;
}

.responsive-image {
	max-width: 100%;
	height: auto;
	display: block;
}

.hide-scrollbar {
	scrollbar-width: none; /* Firefox */
	-ms-overflow-style: none; /* IE and Edge */
	overflow-y: auto;
}

.hide-scrollbar::-webkit-scrollbar {
	display: none; /* Chrome, Safari, Opera */
}

.custom-scrollbar {
	overflow-y: auto;
}

.custom-scrollbar::-webkit-scrollbar {
	width: ${1:10px};
}

.custom-scrollbar::-webkit-scrollbar-track {
	background: ${2:#f1f1f1};
	border-radius: ${3:10px};
}

.custom-scrollbar::-webkit-scrollbar-thumb {
	background: ${4:#888};
	border-radius: ${5:10px};
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
	background: ${6:#555};
}

.grid-gallery {
	display: grid;
	grid-template-columns: repeat(auto-fill, minmax(${1:250px}, 1fr));
	grid-gap: ${2:15px};
}

.grid-gallery-item {
	overflow: hidden;
	border-radius: ${3:8px};
}

.grid-gallery-item img {
	width: 100%;
	height: ${4:250px};
	object-fit: cover;
	transition: transform 0.3s ease;
}

.grid-gallery-item:hover img {
	transform: scale(${5:1.05});
}

:root {
	--text-color: ${1:#333333};
	--bg-color: ${2:#ffffff};
	--card-bg: ${3:#f5f5f5};
}

@media (prefers-color-scheme: dark) {
	:root {
		--text-color: ${4:#f0f0f0};
		--bg-color: ${5:#121212};
		--card-bg: ${6:#1e1e1e};
	}
}

body {
	color: var(--text-color);
	background-color: var(--bg-color);
}

.card {
	background-color: var(--card-bg);
}
